package sec01.exam01;

public class VariableInitializationExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 변수 value 선언
		// value call of variable
		int value = 20;

		// 변수 value 값을 읽고 10을 더하는 산술 연산을 수행
		// Read the value of the variable and perform an arithmetic operation that adds
		// 10

		// 연산의 결과값을 변수 result의 초기값을 저장
		// Store the result value of the operation as the initial value of the variable
		// result
		int result = value + 10;

		// 변수 result 값을 읽고 콘솔에 출력
		// read the result value and print to console
		System.out.println(result);
		
		// Q2)
		// 1. String type name 변수 선언하기
		// 1. String type 'name' variable call
		
		String val = "전창준";
		
		// 2. name 변수에 본인의 이름 저장하기
		// 2. save your name in the 'name' variable
		
		// 3. name의 값을 콘솔창에 출력하기
		// 3. print to console the variable name
		System.out.println(val);
	}

}
